#!/usr/bin/python3
# 7-byte binary encoding

bin = ''
num = len(bin)

for i in range(0,num,7):   # Get every 7th value
    print(chr(int(bin[i:i+7],2)),end='')
    # int(bin[i:i+7]) - Integer in bin from 0 to 0+7
    # (int(bin[i:i+7],2)) - Convert binary to integer
    # chr - Convert integer to ASCII
    # end='' - Remove \n
    
